INSERT INTO `productinfo` VALUES (3, 2, 0, 1, '鱼油胶丸<br>Fish Oil', '葆萃莱鱼油富含DHA、EPA，品质好，纯度高，配比科学。\r\n\r\n\r\n', '<table border="0" width="100%" align="left">\r\n    <tbody>\r\n        <tr>\r\n            <td valign="top" width="50%" align="left">\r\n            <table border="0" width="100%">\r\n                <tbody>\r\n                    <tr>\r\n                        <td><img alt="" width="388" height="281" src="/upload/other/33472897710.jpg" /></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td>\r\n                        <table border="0" width="100%">\r\n                            <tbody>\r\n                                <tr>\r\n                                    <td class="txt_black" height="30" colspan="3" align="left"><span class="txt_prodinfotitle">成分作用</span>:</td>\r\n                                </tr>\r\n                                <tr>\r\n                                    <td class="txt" width="8%">&nbsp;</td>\r\n                                    <td class="txt" width="86%" align="left">\r\n                                    <p>鱼油的主要有效成分叫Omega-3脂肪酸，包括二十碳五烯酸（EPA）、二十二碳六烯酸（DHA），EPA被誉为&ldquo;血管清道夫&rdquo;，DHA被誉为&ldquo;护脑专家&rdquo;，EPA是制造前列腺素的主要成分，能调节血脂、抗血小板凝集、增强心脑血管的健康及肾脏功能；<br />\r\n                                    <br />\r\n                                    DHA可活化脑细胞、有益于眼部健康，改善视力、延缓视力衰退，调节血脂、降低胆固醇和甘油三酯的含量，强化免疫系统。</p>                                    </td>\r\n                                    <td class="txt" width="6%" align="left">&nbsp;</td>\r\n                                </tr>\r\n                            </tbody>\r\n                        </table>\r\n                      </td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td>\r\n                        <table border="0" width="100%">\r\n                            <tbody>\r\n                                <tr>\r\n                                    <td class="txt_black" height="30" colspan="3" align="left"><span class="txt_prodinfotitle">适宜人群</span>:</td>\r\n                                </tr>\r\n                                <tr>\r\n                                    <td class="txt" width="8%">&nbsp;</td>\r\n                                    <td class="txt" height="200" valign="top" width="86%" align="left">三高（高血脂、高血压、高胆固醇）人群；<br />\r\n                                    有血栓、脑溢血及中风等迹象或已患病者；<br />\r\n                                    患有关节炎、哮喘者；<br />\r\n                                    年老而记忆力衰退者；<br />\r\n                                    脂肪肝、便秘及肥胖者；<br />\r\n                                    糖尿病及免疫调节能力较差者。</td>\r\n                                    <td class="txt" valign="top" width="6%" align="left">&nbsp;</td>\r\n                                </tr>\r\n                            </tbody>\r\n                        </table>\r\n                        </td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            </td>\r\n            <td valign="top" width="50%" align="left">\r\n            <table border="0" width="100%">\r\n                <tbody>\r\n                    <tr>\r\n                        <td height="30">\r\n                        <p><strong>鱼油胶丸</strong><strong> Fish Oil</strong></p>\r\n                        </td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td><strong>&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&mdash;&mdash;</strong><strong>改善记忆，增强脑功能</strong><strong> </strong></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td>&nbsp;</td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td class="txt">\r\n                        <p>葆萃莱鱼油富含DHA、EPA，品质好，纯度高，配比科学。</p>\r\n                        <p><strong><br />\r\n                        不适宜人群：少年儿童、孕期及哺乳期的妇女、有出血倾向者和出血性疾病患者。</strong><strong> </strong></p>\r\n                        <p>主要原料：深海鱼油 <br />\r\n                        功效成份及含量：每1000mg含：DHA 160mg，EPA 170mg。 <br />\r\n                        食用方法及食用量：成人每日3次，每次1-2粒。 <br />\r\n                        规格：1000mg*100粒/瓶； <br />\r\n                        1000mg*300粒/瓶</p>\r\n                        </td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td>&nbsp;</td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td height="20">\r\n                        <p><strong>产品特点：</strong><strong> </strong></p>\r\n                        </td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td height="10" valign="top" align="left">&nbsp;</td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td class="txt">\r\n                        <p><br />\r\n                        提取自阿拉斯加及挪威深海野生三文鱼和鲑鱼的鱼油，经分子蒸馏先进技术提取精制而成，含量和纯度高，易于被人体吸收。法国进口明胶制成的软胶囊不会对胃粘膜造成刺激。</p>\r\n                        <p>&nbsp;</p>\r\n                        <p>批准文号：卫食健字（1998）第605号 <br />\r\n                        贮存方法：置于阴凉干燥避光处 <br />\r\n                        保质期：2年 <br />\r\n                        注意事项：本品不能代替药物的治疗作用</p>\r\n                        </td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>', '动物,中老年,明星', 1, 1, '2012-04-17 11:37:23', '2012-06-02 13:57:49', '0000-00-00 00:00:00', 0);
